// Check if retraining is needed
const triggerType = items[0]?.json?.trigger || 'scheduled';
const priority = items[0]?.json?.priority || 'normal';

// Get current time and check last training
const now = new Date();
const lastTraining = new Date(items[0]?.json?.lastTraining || now.getTime() - (24 * 60 * 60 * 1000));
const hoursSinceLastTraining = (now.getTime() - lastTraining.getTime()) / (1000 * 60 * 60);

// Decide if retraining is needed
let shouldRetrain = false;
let reason = '';

if (triggerType === 'scheduled' && hoursSinceLastTraining >= 24) {
  shouldRetrain = true;
  reason = 'Daily scheduled training';
} else if (triggerType === 'critical_risk_detected' && hoursSinceLastTraining >= 1) {
  shouldRetrain = true;
  reason = 'Critical risk pattern detected';
} else if (triggerType === 'manual' || priority === 'high') {
  shouldRetrain = true;
  reason = 'Manual trigger or high priority';
} else if (triggerType === 'feedback_threshold' && hoursSinceLastTraining >= 6) {
  shouldRetrain = true;
  reason = 'Feedback threshold reached';
}

const result = {
  shouldRetrain,
  reason,
  triggerType,
  priority,
  timestamp: now.toISOString(),
  hoursSinceLastTraining
};

console.log(`ðŸ¤– Training check: ${shouldRetrain ? 'RETRAIN' : 'SKIP'} - ${reason}`);

return [{ json: result }];

// Prepare training dataset
const analyticsData = items[0].json || [];
const feedbackData = items[1].json || [];

// Combine and process data
const trainingSet = [];
const validationSet = [];

// Process analytics data
analyticsData.forEach(record => {
  if (record.evidence && record.decision) {
    const features = [];

    // Extract features from evidence
    try {
      const evidence = typeof record.evidence === 'string' ? JSON.parse(record.evidence) : record.evidence;

      if (evidence.edge && Array.isArray(evidence.edge)) {
        evidence.edge.forEach(feature => {
          if (feature.value !== undefined) {
            features.push(feature.value);
          }
        });
      }
    } catch (e) {
      console.warn('Failed to parse evidence:', e);
      return;
    }

    // Ensure we have enough features
    while (features.length < 20) {
      features.push(0);
    }

    // Create label (0 = legitimate, 1 = fraud)
    let label = 0;
    if (record.decision === 'BLOCK' || record.risk_score >= 70) {
      label = 1;
    }

    const sample = {
      features: features.slice(0, 20),
      label,
      weight: 1.0,
      source: 'analytics',
      timestamp: record.created_at
    };

    // 80/20 split for training/validation
    if (Math.random() < 0.8) {
      trainingSet.push(sample);
    } else {
      validationSet.push(sample);
    }
  }
});

// Process feedback data (higher weight)
feedbackData.forEach(feedback => {
  if (feedback.features && feedback.actual_result) {
    try {
      const features = typeof feedback.features === 'string' ? JSON.parse(feedback.features) : feedback.features;

      const sample = {
        features: Array.isArray(features) ? features.slice(0, 20) : [],
        label: feedback.actual_result === 'fraud' ? 1 : 0,
        weight: 2.0, // Higher weight for verified feedback
        source: 'feedback',
        timestamp: feedback.created_at
      };

      // Ensure feature vector length
      while (sample.features.length < 20) {
        sample.features.push(0);
      }

      trainingSet.push(sample);
    } catch (e) {
      console.warn('Failed to process feedback:', e);
    }
  }
});

// Calculate dataset statistics
const stats = {
  totalSamples: trainingSet.length + validationSet.length,
  trainingSamples: trainingSet.length,
  validationSamples: validationSet.length,
  fraudSamples: trainingSet.filter(s => s.label === 1).length + validationSet.filter(s => s.label === 1).length,
  legitimateSamples: trainingSet.filter(s => s.label === 0).length + validationSet.filter(s => s.label === 0).length,
  classBalance: {
    fraud: ((trainingSet.filter(s => s.label === 1).length + validationSet.filter(s => s.label === 1).length) / (trainingSet.length + validationSet.length) * 100).toFixed(2) + '%',
    legitimate: ((trainingSet.filter(s => s.label === 0).length + validationSet.filter(s => s.label === 0).length) / (trainingSet.length + validationSet.length) * 100).toFixed(2) + '%'
  }
};

console.log('ðŸ“Š Dataset prepared:', JSON.stringify(stats, null, 2));

// Check if we have enough data
if (trainingSet.length < 100) {
  throw new Error(`Insufficient training data: ${trainingSet.length} samples (minimum: 100)`);
}

const result = {
  trainingSet,
  validationSet,
  stats,
  timestamp: new Date().toISOString()
};

return [{ json: result }];

// Training failed - analyze and alert
const trainingResult = items[0].json;

const failureAnalysis = {
  accuracy: trainingResult.training_results?.accuracy || 0,
  loss: trainingResult.training_results?.loss || 'unknown',
  reason: 'Low accuracy - model may be overfitting or data quality issues',
  recommendations: [
    'Review training data quality',
    'Adjust hyperparameters',
    'Increase training data size',
    'Check for data leakage'
  ],
  timestamp: new Date().toISOString(),
  severity: 'HIGH'
};

console.log('âŒ Training failed:', JSON.stringify(failureAnalysis, null, 2));

return [{ json: failureAnalysis }];

